class Solution {
public:
    bool isValid(string s) {
        int i,n=s.size();//
        bool flag=true;//
        stack<char>k;
        map<char,int>m={{'(',1},{'{',2},{'[',3},//
                        {')',4},{'}',5},{']',6}};
        for(i=0;i<n;i++)//
        {
            if(m[s[i]]<=3)//
                k.push(s[i]);

            else if(!k.empty()&&m[s[i]]-3==m[k.top()])//
                    k.pop();
            else//
            {
                flag=false;
                break;
            }
        }
        if(!k.empty())//
            flag=false;
        return flag;//

    }
};
```

